import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import Navbar from "./components/Navbar";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import JobTracker from "./pages/JobTracker";

// Optional future page (we can add Dashboard later)
const Dashboard = () => (
  <div className="pt-24 text-center text-white text-3xl font-bold">
    Welcome to CareerPath Portal üöÄ
  </div>
);

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-indigo-900">
        {/* üîù Navbar stays fixed on top */}
        <Navbar />

        {/* üß≠ All routes are defined here */}
        <div className="pt-24 px-4">
          <Routes>
            {/* Redirect the home route to Register */}
            <Route path="/" element={<Navigate to="/register" replace />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/jobtracker" element={<JobTracker />} />

            {/* (Optional) Dashboard route if needed */}
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
