import React, { useState, useEffect } from "react";
import axios from "axios";

const JobTracker = () => {
  const [company, setCompany] = useState("");
  const [position, setPosition] = useState("");
  const [status, setStatus] = useState("Applied");
  const [jobs, setJobs] = useState([]);

  const token = localStorage.getItem("token");

  // Load all jobs for the logged-in user
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/jobs", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setJobs(res.data);
      } catch (err) {
        console.error("Error fetching jobs:", err);
      }
    };
    fetchJobs();
  }, [token]);

  const handleAddJob = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5000/api/jobs",
        { company, position, status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCompany("");
      setPosition("");
      setStatus("Applied");
      // reload job list
      const res = await axios.get("http://localhost:5000/api/jobs", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJobs(res.data);
    } catch (err) {
      console.error("Error adding job:", err);
    }
  };

  return (
    <div className="container" style={{ maxWidth: "600px", margin: "2rem auto" }}>
      <h2 className="text-2xl font-bold mb-4">Career Tracker ðŸ§­</h2>

      <form onSubmit={handleAddJob} className="mb-6">
        <input
          type="text"
          placeholder="Company"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
          required
          className="border p-2 w-full mb-2"
        />
        <input
          type="text"
          placeholder="Position"
          value={position}
          onChange={(e) => setPosition(e.target.value)}
          required
          className="border p-2 w-full mb-2"
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          className="border p-2 w-full mb-2"
        >
          <option>Applied</option>
          <option>Interview Scheduled</option>
          <option>Offer Received</option>
          <option>Rejected</option>
        </select>
        <button
          type="submit"
          className="bg-blue-600 text-white py-2 px-4 rounded w-full"
        >
          Add Job
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">Your Job Applications</h3>
      {jobs.length === 0 ? (
        <p>No jobs added yet.</p>
      ) : (
        <ul className="border p-3 rounded">
          {jobs.map((job) => (
            <li key={job._id} className="mb-2 border-b pb-1">
              <strong>{job.company}</strong> â€” {job.position}  
              <span style={{ float: "right" }}>({job.status})</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default JobTracker;
