import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Profile() {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return navigate("/login");

    fetch("http://localhost:5000/api/users/profile", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setProfile(data.user))
      .catch(() => navigate("/login"));
  }, [navigate]);

  if (!profile)
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-indigo-100 to-purple-200">
        <div className="text-gray-700 text-xl font-semibold">Loading...</div>
      </div>
    );

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-indigo-100 to-purple-200">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-96 text-center">
        <h2 className="text-2xl font-bold text-indigo-700 mb-4">Welcome, User ðŸŽ‰</h2>
        <p className="text-gray-700 mb-2"><strong>Email:</strong> {profile.email}</p>
        <p className="text-gray-700 mb-6"><strong>ID:</strong> {profile.id}</p>
        <button
          onClick={() => {
            localStorage.clear();
            navigate("/login");
          }}
          className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition duration-200"
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default Profile;
